import React from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { Layout, Menu, Breadcrumb, BackTop } from "antd";
import Cookies from "universal-cookie";

import {
  UserOutlined,
  UserAddOutlined,
  SkinOutlined,
  ThunderboltOutlined,
  VideoCameraOutlined,
  StarOutlined,
} from "@ant-design/icons";

import LoginSignup from "./routes/login_signup";
import Home from "./routes/home";
import Avatar from "antd/lib/avatar/avatar";
import Profile from "./routes/profile";

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

class App extends React.Component {
  state = {
    collapsed: false,
  };

  logout = () => {
    const cookies = new Cookies();
    cookies.remove("session");
    window.location.href = "/login";
  };

  onCollapse = (collapsed: any) => {
    //console.log(collapsed);
    this.setState({ collapsed });
  };

  render() {
    const { collapsed } = this.state;
    const cookies = new Cookies();
    const session: any = cookies.get("session");

    return (
      <Layout style={{ minHeight: "100vh" }}>
        <Sider
          className="sidebar"
          collapsible
          collapsed={collapsed}
          onCollapse={this.onCollapse}
          style={{
            overflow: "auto",
            height: "100vh",
            position: "sticky",
            top: 0,
            left: 0,
          }}
        >
          <Menu className="menu" mode="inline">
            <Link to={"/"}>
              {" "}
              <div className="logo">
                <h5>FM</h5>
              </div>{" "}
            </Link>
            {session === undefined ? (
              <Menu.Item key="login" icon={<UserOutlined />}>
                <Link style={{ color: "var(--color-primary)" }} to={"/login"}>
                  Login/Signup
                </Link>
              </Menu.Item>
            ) : (
              <SubMenu
                key="user"
                icon={
                  <Avatar
                    style={{
                      color: "#fff",
                      backgroundColor: "var(--color-primary)",
                    }}
                  >
                    {session.name.charAt(0)}
                  </Avatar>
                }
                title={session.name}
              >
                <Menu.Item key="profile">
                  <Link to={"/profile/" + session.token}>Profile</Link>
                </Menu.Item>
                <Menu.Item key="settings">Settings</Menu.Item>
              </SubMenu>
            )}

            <SubMenu
              key="appliance"
              icon={<ThunderboltOutlined />}
              title="Appliance"
            >
              <Menu.Item key="3">Tom</Menu.Item>
              <Menu.Item key="4">Bill</Menu.Item>
              <Menu.Item key="5">Alex</Menu.Item>
            </SubMenu>
            <SubMenu key="clothing" icon={<SkinOutlined />} title="clothing">
              <Menu.Item key="6">Team 1</Menu.Item>
              <Menu.Item key="7">Team 2</Menu.Item>
            </SubMenu>
            <SubMenu
              key="photo"
              icon={<VideoCameraOutlined />}
              title="Photo and Video"
            >
              <Menu.Item key="8">Tom</Menu.Item>
              <Menu.Item key="9">Bill</Menu.Item>
              <Menu.Item key="10">Alex</Menu.Item>
            </SubMenu>
            <SubMenu key="hobby" icon={<StarOutlined />} title="Hobby">
              <Menu.Item key="11">Tom</Menu.Item>
              <Menu.Item key="12">Bill</Menu.Item>
              <Menu.Item key="13">Alex</Menu.Item>
            </SubMenu>
            {session !== undefined && (
              <Menu.Item
                key="logout"
                style={{ background: "#f37370", color: "#fff" }}
                onClick={this.logout}
              >
                Logout
              </Menu.Item>
            )}
          </Menu>
        </Sider>
        <Layout id="siteLayout" className="site-layout">
          <Header
            className="site-layout-background-header"
            style={{ padding: 0 }}
          >
            <h1>flea market</h1>
          </Header>
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item className="breadcrump-item">
                Home
              </Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background-content"
              id="content-layout"
              style={{ padding: 24, minHeight: "100%" }}
            >
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/profile/:id" element={<Profile />} />
              </Routes>
            </div>
          </Content>
          <Footer
            className="site-layout-background-footer"
            style={{ textAlign: "center" }}
          >
            Ant Design ©2018 Created by Ant UED
          </Footer>
        </Layout>
        <BackTop />
      </Layout>
    );
  }
}
export default App;
