import React from "react";
import { ScrollMenu, VisibilityContext } from "react-horizontal-scrolling-menu";

import { Cards } from "../component/home/card";
import { LeftArrow, RightArrow } from "../component/home/arrows";

type scrollVisibilityApiType = React.ContextType<typeof VisibilityContext>;

const elemPrefix = "test";
const eleMeta = "price";
const getId = (index: number) => `${elemPrefix}${index}`;
const getMeta = (index: number) => `${eleMeta}${index}`;

const getItemsAppl = () =>
  Array(10)
    .fill(0)
    .map((_, ind) => ({ id: getId(ind), metas: getMeta(ind) }));
const getItemsClot = () =>
  Array(20)
    .fill(0)
    .map((_, ind) => ({ id: getId(ind), metas: getMeta(ind) }));

function Home() {
  const [items] = React.useState(getItemsAppl);
  const [itemsClo] = React.useState(getItemsClot);

  return (
    <>
      <div className="example" style={{ paddingTop: "100px" }}>
        <h6>best clothing with more observations</h6>
        <div>
          <ScrollMenu
            LeftArrow={LeftArrow}
            RightArrow={RightArrow}
            options={{
              ratio: 0.9,
              rootMargin: "5px",
              threshold: [0.01, 0.05, 0.5, 0.75, 0.95, 1],
            }}
          >
            {items.map(({ id, metas }) => (
              <Cards
                title={id}
                itemId={id} // NOTE: itemId is required for track items
                meta={metas}
                key={id}
              />
            ))}
          </ScrollMenu>
        </div>
      </div>
      <div className="example" style={{ paddingTop: "100px" }}>
        <h6>best appliance product with more observations</h6>
        <div>
          <ScrollMenu
            LeftArrow={LeftArrow}
            RightArrow={RightArrow}
            options={{
              ratio: 0.9,
              rootMargin: "5px",
              threshold: [0.01, 0.05, 0.5, 0.75, 0.95, 1],
            }}
          >
            {itemsClo.map(({ id, metas }) => (
              <Cards
                title={id}
                itemId={id} // NOTE: itemId is required for track items
                meta={metas}
                key={id}
              />
            ))}
          </ScrollMenu>
        </div>
      </div>
    </>
  );
}
export default Home;
