import React from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { Layout, Menu, Breadcrumb } from "antd";
import {
  UserOutlined,
  UserAddOutlined,
  SkinOutlined,
  ThunderboltOutlined,
  VideoCameraOutlined,
  StarOutlined,
} from "@ant-design/icons";
import Login from "./routes/login";
import Signup from "./routes/signup";
import Home from "./routes/home";

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

class App extends React.Component {
  state = {
    collapsed: false,
    //routes: "",
  };

  componentDidMount() {
    if (
      window.location.pathname === "/login" ||
      window.location.pathname === "/signup"
    ) {
      console.log(window.location.pathname);
      let lay: any = document.getElementById("content-layout");
      lay.classList.add("access");
    } else {
      console.log(window.location.pathname);
      let lay: any = document.getElementById("content-layout");
      lay.classList.remove("access");
    }
  }
  /*access = (ele: string) => {
    if (ele === "t") {
      let lay: any = document.getElementById("content-layout");
      lay.classList.add("access");
    } else {
      let lay: any = document.getElementById("content-layout");
      lay.classList.remove("access");
    }
  };*/

  onCollapse = (collapsed: any) => {
    console.log(collapsed);
    this.setState({ collapsed });
  };

  render() {
    const { collapsed } = this.state;
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <Sider
          className="sidebar"
          collapsible
          collapsed={collapsed}
          onCollapse={this.onCollapse}
        >
          <Menu className="menu" mode="inline">
            <Link to={"/"}>
              {" "}
              <div className="logo">
                <h5>FM</h5>
              </div>{" "}
            </Link>
            <Menu.Item key="login" icon={<UserOutlined />}>
              <Link style={{ color: "var(--color-primary)" }} to={"/login"}>
                Login
              </Link>
            </Menu.Item>
            <Menu.Item key="signup" icon={<UserAddOutlined />}>
              <Link style={{ color: "var(--color-primary)" }} to={"/signup"}>
                Signup
              </Link>
            </Menu.Item>
            <SubMenu
              key="appliance"
              icon={<ThunderboltOutlined />}
              title="Appliance"
            >
              <Menu.Item key="3">Tom</Menu.Item>
              <Menu.Item key="4">Bill</Menu.Item>
              <Menu.Item key="5">Alex</Menu.Item>
            </SubMenu>
            <SubMenu key="clothing" icon={<SkinOutlined />} title="clothing">
              <Menu.Item key="6">Team 1</Menu.Item>
              <Menu.Item key="7">Team 2</Menu.Item>
            </SubMenu>
            <SubMenu
              key="photo"
              icon={<VideoCameraOutlined />}
              title="Photo and Video"
            >
              <Menu.Item key="8">Tom</Menu.Item>
              <Menu.Item key="9">Bill</Menu.Item>
              <Menu.Item key="10">Alex</Menu.Item>
            </SubMenu>
            <SubMenu key="hobby" icon={<StarOutlined />} title="Hobby">
              <Menu.Item key="11">Tom</Menu.Item>
              <Menu.Item key="12">Bill</Menu.Item>
              <Menu.Item key="13">Alex</Menu.Item>
            </SubMenu>
          </Menu>
        </Sider>
        <Layout className="site-layout">
          <Header
            className="site-layout-background-header"
            style={{ padding: 0 }}
          >
            <h1>flea market</h1>
          </Header>
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>User</Breadcrumb.Item>
              <Breadcrumb.Item>Bill</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background-content"
              id="content-layout"
              style={{ padding: 24, minHeight: "85%" }}
            >
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="login" element={<Login />} />
                <Route path="signup" element={<Signup />} />
              </Routes>
            </div>
          </Content>
          <Footer
            className="site-layout-background-footer"
            style={{ textAlign: "center" }}
          >
            Ant Design ©2018 Created by Ant UED
          </Footer>
        </Layout>
      </Layout>
    );
  }
}
export default App;
