import React from "react";
import { PageHeader, Button, Descriptions, Select } from "antd";
import { Row, Col, Divider } from "antd";
import { Cards } from "../component/home/card";
const { Option } = Select;

class Announce extends React.Component {
  elemPrefix: any = "test";
  eleMeta: any = "price";
  getId = (index: number) => `${this.elemPrefix}${index}`;
  getMeta = (index: number) => `${this.eleMeta}${index}`;
  getItems = () =>
    Array(10)
      .fill(0)
      .map((_, ind) => ({ id: this.getId(ind), metas: this.getMeta(ind) }));
  state = {
    items: this.getItems(),
  };
  render(): React.ReactNode {
    const style = { background: "#0092ff", padding: "8px 0" };
    return (
      <div className="site-page-header-ghost-wrapper">
        <PageHeader
          ghost={false}
          onBack={() => window.history.back()}
          title="Title"
          subTitle="This is a subtitle"
          extra={[
            <Select size="large" placeholder="filter by" style={{ width: 200 }}>
              <Option key={1}>filter1</Option>
              <Option key={2}>"filter1"</Option>
              <Option key={3}>"filter1"</Option>
            </Select>,
          ]}
        ></PageHeader>
        <Divider orientation="left"></Divider>
        <Row gutter={[24, 24]}>
          <Col key={1} span={8}>
            {this.state.items.map(({ id, metas }) => (
              <Cards
                title={id}
                itemId={id} // NOTE: itemId is required for track items
                meta={metas}
                key={id}
              />
            ))}
          </Col>
        </Row>
      </div>
    );
  }
}
export default Announce;
