import React from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { Layout, Menu, Breadcrumb, BackTop } from "antd";
import Cookies from "universal-cookie";

import {
  UserOutlined,
  UserAddOutlined,
  SkinOutlined,
  ThunderboltOutlined,
  VideoCameraOutlined,
  StarOutlined,
} from "@ant-design/icons";

import LoginSignup from "./routes/login_signup";
import Home from "./routes/home";
import Avatar from "antd/lib/avatar/avatar";
import Profile from "./routes/profile";
import Announce from "./routes/announce";

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

class App extends React.Component {
  state = {
    collapsed: false,
  };
  Itemsubmenu = () => {
    const category_subcategory: object = {
      appliance: {
        category: "appliance",
        subcategory: {
          sub1: "vacuum",
          sub2: "coffee pots",
          sub3: "toaster",
          sub4: "blenders",
        },
        link: {
          link1: "vacuum",
          link2: "coffeepots",
          link3: "toaster",
          link4: "blenders",
        },
      },
      clothing: {
        category: "clothing",
        subcategory: {
          sub1: "clothes",
          sub2: "purse",
          sub3: "shoes",
          sub4: "fitting clothes",
        },
        link: {
          link1: "clothes",
          link2: "purse",
          link3: "shoes",
          link4: "fittingclothes",
        },
      },
      photovideo: {
        category: "photo/video",
        subcategory: {
          sub1: "cameras",
          sub2: "fitting video",
          sub3: "telecamera",
          sub4: "microphones",
        },
        link: {
          link1: "cameras",
          link2: "fittingvideo",
          link3: "telecamera",
          link4: "microphones",
        },
      },
      hobby: {
        category: "hobby",
        subcategory: {
          sub1: "toys",
          sub2: "films/dvds",
          sub3: "music",
          sub4: "books/magazines",
        },
        link: {
          link1: "toys",
          link2: "filmsdvds",
          link3: "music",
          link4: "booksmagazines",
        },
      },
    };
    let group = [];
    for (const [key, value] of Object.entries(category_subcategory)) {
      console.log(key, value);
      group.push(
        <SubMenu
          key={value.category}
          className="submenu-active"
          title={
