import React from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { Layout, Menu, Breadcrumb, BackTop } from "antd";
import Cookies from "universal-cookie";

import {
  UserOutlined,
  UserAddOutlined,
  SkinOutlined,
  ThunderboltOutlined,
  VideoCameraOutlined,
  StarOutlined,
} from "@ant-design/icons";

import LoginSignup from "./routes/login_signup";
import Home from "./routes/home";
import Avatar from "antd/lib/avatar/avatar";
import Profile from "./routes/profile";
import { Submenu } from "./component/sidebar/submenu";

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

class App extends React.Component {
  state = {
    collapsed: false,
  };

  logout = () => {
    const cookies = new Cookies();
    cookies.remove("session");
    window.location.href = "/login";
  };

  onCollapse = (collapsed: any) => {
    //console.log(collapsed);
    this.setState({ collapsed });
  };

  render() {
    const { collapsed } = this.state;
    const cookies = new Cookies();
    const session: any = cookies.get("session");

    return (
      <Layout style={{ minHeight: "100vh" }}>
        <Sider
          className="sidebar"
          collapsible
          collapsed={collapsed}
          onCollapse={this.onCollapse}
          width={240}
        >
          <Menu className="menu" mode="inline">
            <Link to={"/"}>
              {" "}
              <div className="logo">
                <h5>FM</h5>
              </div>{" "}
            </Link>
            {session === undefined ? (
              <Menu.Item key="login" icon={<UserOutlined />}>
                <Link className="link-menu" to={"/login"}>
                  Login/Signup
                </Link>
              </Menu.Item>
            ) : (
              <SubMenu
                key="user"
                icon={
                  <Avatar className="avatar-sidebar-menu">
                    {session.name.charAt(0)}
                  </Avatar>
                }
                title={session.name}
              >
                <Menu.Item key="profile">
                  <Link to={"/profile/" + session.token}>Profile</Link>
                </Menu.Item>
                <Menu.Item key="settings">Settings</Menu.Item>
              </SubMenu>
            )}

            <Submenu id="app" />
            <SubMenu
              key="appliance"
              className="submenu-active"
              icon={<ThunderboltOutlined />}
              title="Appliance"
            >
              <Menu.Item key="vacuum">
                <Link to="/appliance/vacum" className="link-submenu">
                  vacuum
                </Link>{" "}
              </Menu.Item>
              <Menu.Item key="coffee">coffee pots</Menu.Item>
              <Menu.Item key="toaster">toaster</Menu.Item>
              <Menu.Item key="blenders">blenders</Menu.Item>
            </SubMenu>
            <SubMenu key="clothing" icon={<SkinOutlined />} title="clothing">
              <Menu.Item key="clothes">clothes</Menu.Item>
              <Menu.Item key="purse">purse</Menu.Item>
              <Menu.Item key="shoes">shoes</Menu.Item>
              <Menu.Item key="fittingClo">fitting</Menu.Item>
            </SubMenu>
            <SubMenu
              key="photo"
              icon={<VideoCameraOutlined />}
              title="Photo/Video"
            >
              <Menu.Item key="cameras">cameras</Menu.Item>
              <Menu.Item key="fittingPho">fitting</Menu.Item>
              <Menu.Item key="telecamera">telecamera</Menu.Item>
              <Menu.Item key="microphones">microphones</Menu.Item>
            </SubMenu>
            <SubMenu key="hobby" icon={<StarOutlined />} title="Hobby">
              <Menu.Item key="toys">toys</Menu.Item>
              <Menu.Item key="films/dvd">films/DVDs</Menu.Item>
              <Menu.Item key="music">music</Menu.Item>
              <Menu.Item key="books_magazines">books/magazines</Menu.Item>
            </SubMenu>
            {session !== undefined && (
              <Menu.Item
                key="logout"
                style={{ background: "#f37370", color: "#fff" }}
                onClick={this.logout}
              >
                Logout
              </Menu.Item>
            )}
          </Menu>
        </Sider>
        <Layout id="siteLayout" className="site-layout">
          <Header
            className="site-layout-background-header"
            style={{ padding: 0 }}
          >
            <h1>flea market</h1>
          </Header>
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item className="breadcrump-item">
                Home
              </Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background-content"
              id="content-layout"
              style={{ padding: 24, minHeight: "100%" }}
            >
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/profile/:id" element={<Profile />} />
              </Routes>
            </div>
          </Content>
          <Footer
            className="site-layout-background-footer"
            style={{ textAlign: "center" }}
          >
            Ant Design Â©2018 Created by Ant UED
          </Footer>
        </Layout>
        <BackTop />
      </Layout>
    );
  }
}
export default App;
