  logout = () => {
    const cookies = new Cookies();
    cookies.remove("session");
    window.location.href = "/login";
  };

  onCollapse = (collapsed: any) => {
    //console.log(collapsed);
    this.setState({ collapsed });
  };

  render() {
    const { collapsed } = this.state;
    const cookies = new Cookies();
    const session: any = cookies.get("session");

    return (
      <Layout style={{ minHeight: "100vh" }}>
        <Sider
          className="sidebar"
          collapsible
          collapsed={collapsed}
          trigger={null}
          onCollapse={this.onCollapse}
          width={240}
        >
          <Menu className="menu" mode="vertical">
            <a href="/">
              <div className="logo">
                <h5>FM</h5>
              </div>
            </a>
            {session === undefined ? (
              <Menu.Item key="login" icon={collapsed && <UserOutlined />}>
                <Link className="link-menu" to={"/login"}>
                  Login/Signup
                </Link>
              </Menu.Item>
            ) : (
              <>
                <SubMenu
                  key="user"
                  icon={
                    <Avatar
                      style={{
                        backgroundColor: "var(--color-orange)",
                        color: "#fff !important",
                        verticalAlign: "middle",
                      }}
                      size="default"
                      gap={2}
                    >
                      {session.name.charAt(0)}
                    </Avatar>
                  }
                  title={session.name}
                >
                  <Menu.Item key="profile">
                    <Link
                      className="link-menu"
                      to={"/profile/" + session.token}
                    >
                      Profile
                    </Link>
                  </Menu.Item>
                  <Menu.Item key="settings">
                    {" "}
                    <Link
                      className="link-menu"
                      to={"/profile/settings/" + session.token}
                    >
                      Settings
                    </Link>
                  </Menu.Item>
                  <Menu.Item
                    key="logout"
                    className="sidebar-logout"
                    onClick={this.logout}
                  >
                    Logout
                  </Menu.Item>
                </SubMenu>
                <Divider
                  orientation="left"
                  style={{
                    borderBottom: ".25em solid #fff",
                    opacity: ".5",
                  }}
                ></Divider>
              </>
            )}
            <ItemGrup />
          </Menu>
        </Sider>
        <Layout id="siteLayout" className="site-layout">
          <Header
            className="site-layout-background-header"
            style={{ padding: 0 }}
          >
            <h1>flea market</h1>
          </Header>
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item className="breadcrump-item">
                Home
              </Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background-content"
              id="content-layout"
              style={{ padding: 24, minHeight: "100%" }}
            >
              <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/profile/:id" element={<Profile />} />
                <Route path="/announce/:id/:id" element={<Announce />} />
              </Routes>
            </div>
          </Content>
          <Footer
            className="site-layout-background-footer"
            style={{ textAlign: "center" }}
          >
            Ant Design ©2018 Created by Ant UED
          </Footer>
        </Layout>
        <BackTop />
      </Layout>
    );
  }
}
export default App;

